    private DataFetcher dataFetcher = new DataFetcher();

    @Test
    public void testRenderNumberOfPacketByDay_isCorrect() {
        JSONObject data = null;

        try {
            data = new JSONObject(jsonStringDay);

        } catch (JSONException e) {
            e.printStackTrace();
        }
        ArrayList<WeatherInformationPacket> temp = dataFetcher.renderWeatherDataByDay(data);

        assertTrue(temp.size() == 3);
    }

    @Test
    public void testRenderDate_isCorrect() {
        JSONObject data = null;

        try {
            data = new JSONObject(jsonStringDay);
        } catch (JSONException e) {
            e.printStackTrace();
        }
        ArrayList<WeatherInformationPacket> temp = dataFetcher.renderWeatherDataByDay(data);
        DateFormat df = new SimpleDateFormat("yyyy-MM-dd");
        Date startDate = null, nextDate = null;

        try {
            startDate = df.parse(temp.get(0).Date);
            nextDate = df.parse(temp.get(1).Date);
        } catch (ParseException e) {
            e.printStackTrace();
        }
        assertTrue(TimeUnit.DAYS.convert(nextDate.getTime() - startDate.getTime(), TimeUnit.MILLISECONDS) == 1);
    }

    @Test
    public void testRenderAttributeDay_isCorrect() {
        JSONObject data = null;

        try {
            data = new JSONObject(jsonStringDay);
        } catch (JSONException e) {
            e.printStackTrace();
        }
        ArrayList<WeatherInformationPacket> temp = dataFetcher.renderWeatherDataByDay(data);
        assertTrue(temp.get(0).country.equals("Chad"));
    }

    @Test
    public void testRenderSameValueAttributeDay_isCorrect() {
        JSONObject data = null;

        try {
            data = new JSONObject(jsonStringDay);
        } catch (JSONException e) {
            e.printStackTrace();
        }
        ArrayList<WeatherInformationPacket> temp = dataFetcher.renderWeatherDataByDay(data);
        assertTrue(temp.get(0).areaName.equals(temp.get(2).areaName));
    }

